import {useState} from 'react';

function Button() {
  const [count,setCount] = useState(0);

    return (
        <div>
        <p>Вы кликнули {count} раз</p>
               <button className='btn' onClick={() => setCount(count + 1)}>
                 Увеличить
                </button>
                <button className='btn' onClick={() => setCount(count -1)}>
                   Уменьшить
                </button>
        </div>
       );
}

export default Button;
